clear all;clc;pkg load optim;%chutes iniciaisx0 = 1;y0 = 2;%equaçõesu = @(x)[x(1)^2+x(1)*x(2)-10];v = @(x)[x(2)+3*x(1)*x(2)^2-57];F(1) = u([x0,y0]);F(2) = v([x0,y0]);%aplicação do métodowhile (abs(F(1)) >= 10e-3 && (abs(F(2)) >= 10e-3))  JU = jacobs([x0,y0],u);JV = jacobs([x0,y0],v);Z = [JU;JV];C = [x0;y0];B = -F' + Z*C;C = inv(Z)*B;x0 = C(1);y0 = C(2);endwhileF'x = C(1)y = x(2)